/**
 * @description This code iterates through all input items, sets the MIME type of the binary data to 'image/jpeg', and converts the binary data to a Base64 string.
 * @returns {Array} An array of items with the binary data converted to Base64.
 */
for (const item of $input.all()) {
    // Manually set the correct MIME type
    item.binary.data.mimeType = "image/jpeg";

    // Convert binary to Base64
    item.json.base64Image = `data:image/jpeg;base64,${item.binary.data.data.toString('base64')}`;
}

return $input.all();
